function chunkArrayInGroups(arr, size) {
  let arrToReturn = [];
  let tempArrChunk = [];
  for (let i=0; i<arr.length; i++){
    tempArrChunk.push(arr[i]);
    if (tempArrChunk.length == size || i==arr.length-1){
      arrToReturn.push(tempArrChunk);
      tempArrChunk = [];
    }
  }  return arrToReturn;
}

chunkArrayInGroups(["a", "b", "c", "d"], 2);
