function diffArray(arr1, arr2) {
  let newArr = [];
  for (let i = 0; i<arr1.length; i++){
    if (!isInTwoArrays(arr1,arr2,arr1[i])){
      newArr.push(arr1[i]);
    }
  }

  for (let i = 0; i<arr2.length; i++){
    if (!isInTwoArrays(arr1,arr2,arr2[i])){
      if (!newArr.includes(arr2[i])){
        newArr.push(arr2[i]);
      }
    }
  }
  return newArr;
}

function isInTwoArrays(arr1,arr2,elem){
  let flag = true;
  if (!arr1.includes(elem) || !arr2.includes(elem)){
    flag = false;
  }
  return flag;
}


diffArray([1, 2, 3, 5], [1, 2, 3, 4, 5]);
