function findLongestWordLength(str) {
  let strArr = str.split(" ");
  let j = 0;
  for (let i = 0; i<strArr.length; i++){
    if (strArr[i].length > j){
      j = strArr[i].length;
    }
  }
  console.log(j);
  return j;
}

