function mutation(arr) {
  let scndArrayLetters = arr[1].split("");
  let flag = true;
  for (let i = 0; i<scndArrayLetters.length; i++){
    if (!arr[0].toLowerCase().includes(scndArrayLetters[i].toLowerCase())){
      flag = false;
    }
  }

  return flag;
}

mutation(["hello", "hey"]);
